# -*- coding: utf-8 -*-
import requests
import time
import datetime
import re
import random














class XiaomiSport:
    def __init__(self, phone, password, step):
        # 手机号
        self.phone = '+86' + phone
        # 密码
        self.password = password
        # 修改步数
        self.step = step
        # 通行码
        self.access_code = ''
        # app令牌
        self.app_token = ''
        # 用户id
        self.user_id = ''

    # 登录
    def login(self):
        url = 'https://api-user.huami.com/registrations/' + self.phone + '/tokens'
        data = {
            'phone_number': self.phone,
            'password': self.password,
            'client_id': 'HuaMi',
            'token': 'access',
            'redirect_uri': 'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html'
        }
        headers = {
            'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
            'User-Agent': 'Dalvik/2.1.0 (Linux; U; Android 5.1.1; SM-G9750 Build/LMY48Z)'
        }
        response = requests.post(url, data=data, headers=headers)
        # print('登录状态码：%s' % response.status_code)
        if response.status_code != 200:
            return '登录失败'
        else:
            # 重定向的Location
            redirect_location = response.history[0].headers['Location']
            # print(redirect_location)
            # 获取通行码
            find_access = re.compile(r'access=(.*?)&')
            access_code = find_access.findall(redirect_location)
            # 无论账号密码是否对得上，响应状态码都是200，但如果账号密码对不上，是没有访问码的
            if len(access_code) == 0:
                return '账号密码对不上，登录失败'
            else:
                self.access_code = access_code[0]
                # print('通行码：%s' % self.access_code)
                return '登录成功'

    # 获取令牌
    def get_token(self):
        url = 'https://account.huami.com/v2/client/login'
        data = {
            'third_name': 'huami_phone',
            'grant_type': 'access_token',
            'device_model': 'android_phone',
            'device_id': '865166029072108',
            'country_code': 'CN',
            'code': self.access_code,
            'app_version': '4.6.1',
            'app_name': 'com.xiaomi.hm.health'
        }
        headers = {
            'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
            'User-Agent': 'Dalvik/2.1.0 (Linux; U; Android 5.1.1; SM-G9750 Build/LMY48Z)'
        }
        response = requests.post(url, data=data, headers=headers)
        # print('获取令牌状态码：%s' % response.status_code)
        # print('获取令牌返回数据：%s' % response.text)
        if response.status_code != 200:
            return '获取令牌失败'
        else:
            if len(eval(response.text)) == 1:
                return '获取令牌失败'
            else:
                token = eval(response.text)['token_info']
                self.app_token = token['app_token']
                # print('app_token: %s' % self.app_token)
                self.user_id = token['user_id']
                # print('user_id: %s' % self.user_id)
                return '获取令牌成功'

